// Resume Optimizer - Tailors resume to job description
const jobData = items[0].json;
const { job_description, current_resume } = jobData;

// Extract key requirements from job description
const extractKeywords = (text) => {
  const techKeywords = ['python', 'javascript', 'react', 'node.js', 'sql', 'aws', 'docker'];
  const found = [];

  for (const keyword of techKeywords) {
    if (text.toLowerCase().includes(keyword)) {
      found.push(keyword);
    }
  }

  return found;
};

const requiredSkills = extractKeywords(job_description);

// Generate optimized resume sections
const optimizedResume = {
  summary: `Experienced professional with expertise in ${requiredSkills.join(', ')}`,
  skills: requiredSkills,
  experience_highlights: [
    `Developed applications using ${requiredSkills[0] || 'modern technologies'}`,
    `Led projects involving ${requiredSkills[1] || 'software development'}`,
    `Collaborated on ${requiredSkills[2] || 'technical'} initiatives`
  ],
  match_percentage: Math.min(95, requiredSkills.length * 15 + 25)
};

return [{ json: optimizedResume }];
